import { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";
import logo from "../assets/logo.png";

const Register = (props) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');

  const data = {
    'email': email,
    'password': password,
    'firstName': firstName,
    'lastName': lastName
  }

  const handleRegister = (event) => {
    event.preventDefault();
  };

  const handleEmail = (event) => {
    event.preventDefault();
  };

  const handlePassword = (event) => {
    event.preventDefault();
  };

  const handleFirstName = (event) => {
    event.preventDefault();
  };

  const handleLastName = (event) => {
    event.preventDefault();
  };

  return (
    <Container className="vh-100 d-flex align-items-center justify-content-center">
      <Card className="col-12 col-md-8 col-lg-6 col-xl-5">
        <Card.Body className="p-5 text-center">
          <Form>
            <img src={logo} alt="UCF logo" />
            <h3 className="text-center my-5">Register for an account</h3>
            <Form.Group className="mb-3" controlId="formFirstName">
              <Form.Floating>
                <Form.Control type="First Name" placeholder="First Name" onChange={handleFirstName}/>
                <Form.Label>First Name</Form.Label>
              </Form.Floating>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formLastName">
              <Form.Floating>
                <Form.Control type="Last Name" placeholder="Last Name" onChange={handleLastName}/>
                <Form.Label>Last Name</Form.Label>
              </Form.Floating>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formEmail">
              <Form.Floating>
                <Form.Control type="email" placeholder="Email" />
                <Form.Label>Email</Form.Label>
              </Form.Floating>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formPassword">
              <Form.Floating>
                <Form.Control type="password" placeholder="Password" />
                <Form.Label>Password</Form.Label>
              </Form.Floating>
            </Form.Group>
            <Button onSubmit={handleRegister} variant="primary" className="mt-1">
              Register
            </Button>
          </Form>
        </Card.Body>
        <Card.Footer className="text-muted">
          <span>Already Registered? </span>
          <a
            href="javascript:void(0);"
            onClick={() => props.onFormSwitch("login")}
          >
            Sign in
          </a>
        </Card.Footer>
      </Card>
    </Container>
  );
};

export default Register;
