import { useState } from "react";
import { LoginAPI } from "../api_handler/Users";
import { Link } from "react-router-dom";
import FormLayout from "../layouts/FormLayout";a
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";
import logo from "../assets/logo.png";

const Login = (props) => {
  const [formValues, setFormValues] = useState({
    email: "",
    password: "",
  });

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log(formValues);

    LoginAPI(formValues);

    setFormValues({
      email: "",
      password: "",
    });
  };

  const handleChange = (event) => {
    event.preventDefault();
    setFormValues({ ...formValues, [event.target.name]: event.target.value });
  };

  return (
    <BackgroundLayout>
      <Container className="vh-100 d-flex align-items-center justify-content-center">
        <Card className="col-12 col-md-8 col-lg-6 col-xl-5">
          <Card.Body className="p-5 text-center">
            <Form onSubmit={handleSubmit}>
              <img src={logo} alt="UCF logo" />
              <h3 className="text-center my-5">Sign in to your account</h3>
              <Form.Group className="mb-3" controlId="formEmail">
                <Form.Floating>
                  <Form.Control
                    value={formValues.email}
                    name="email"
                    type="email"
                    placeholder="Email"
                    onChange={handleChange}
                    required
                  />
                  <Form.Label>Email</Form.Label>
                </Form.Floating>
              </Form.Group>
              <Form.Group className="mb-3" controlId="formPassword">
                <Form.Floating>
                  <Form.Control
                    value={formValues.password}
                    name="password"
                    type="password"
                    placeholder="Password"
                    onChange={handleChange}
                    required
                  />
                  <Form.Label>Password</Form.Label>
                </Form.Floating>
              </Form.Group>
              <Button type="submit" variant="primary" className="mt-1">
                Sign In
              </Button>
            </Form>
          </Card.Body>
          <Card.Footer className="text-muted">
            <span>New User? </span>
            <Link to="/register">Register</Link>
          </Card.Footer>
        </Card>
      </Container>
    </BackgroundLayout>
  );
};

export default Login;
